import React, { useEffect, useState, Fragment } from 'react';
import { useParams } from 'react-router-dom';
import { Card, Container, Button } from 'react-bootstrap';
import "./SingleItemPage.css";

import axios from 'axios';

export default function SingleItemPage(props) {
  const { id } = useParams();

  const [appState, setAppState] = useState([]);

  async function getBook() {
    const item = await axios.get(`http://127.0.0.1:5000/book/${id}`);
    console.log(item.data);
    setAppState(item.data);
  }

  useEffect(() => getBook(), []);


  return (
    <Fragment>

      <Button variant="outline-dark" className="item-page-button">
        Add to Cart
      </Button>

      <Container>
        <div style={{ marginTop: 80 + 'px' }}></div>

        <Card>
          <Card.Img style={{ height: 200 + 'px' }} variant="top" src={appState.imgUrl} />
          <Card.Body>
            <Card.Title className="text-center">{appState[1]}</Card.Title>
            <Card.Text className="text-center">{appState[2]}</Card.Text>
            <div
              style={{ marginTop: 20 + 'px', marginBottom: 20 + 'px', height: 1 + 'px', backgroundColor: 'grey' }}
            ></div>
            <div className="text-center">
              <h1 className="price">Price: {appState[3]} $</h1>
            </div>
          </Card.Body>
        </Card>
      </Container>



      <div style={{ marginTop: 80 + 'px' }}></div>
    </Fragment>
  );
}
